//Evaluate whether the given number n can be written as the sum of exactly 4 positive even numbers
// Example
// >>> is_equal_to_sum_even(4)
// false
// >>> is_equal_to_sum_even(6)
// false
// >>> is_equal_to_sum_even(8)
// true
function is_equal_to_sum_even(n: number): boolean {
  const sum = n / 2;
  const even = sum % 2 === 0;
  const positive = sum > 0;
  const even_positive = even && positive;
  const sum_even = sum % 2 === 0;
  const sum_even_positive = sum_even && positive;
  return even_positive || sum_even_positive;
}
