// false
// >>> pairs_sum_to_zero([1, 3, -2, 1])
// false
// >>> pairs_sum_to_zero([1, 2, 3, 7])
// false
// >>> pairs_sum_to_zero([2, 4, -5, 3, 5, 7])
// true
// >>> pairs_sum_to_zero([1])
// false
function pairs_sum_to_zero(l: number[]): boolean {
  const map = new Map<number, number>();
  for (let i = 0; i < l.length; i++) {
    const key = l[i];
    if (map.has(key)) {
      return true;
    }
    map.set(key, i);
  }
  return false;
}
