//Given array of integers, return array in strange order.
// Strange sorting, is when you start with the minimum value,
// then maximum of the remaining integers, then minimum and so on.
// Examples:
// >>> strange_sort_list([1, 2, 3, 4])
// [1, 4, 2, 3]
// >>> strange_sort_list([5, 5, 5, 5])
// [5, 5, 5, 5]
// >>> strange_sort_list([])
// []
function strange_sort_list(lst: number[]): number[] {
  if (lst.length === 0) {
    return [];
  }
  const min = Math.min(...lst);
  const max = Math.max(...lst);
  const sorted = lst.sort((a, b) => a - b);
  const sorted_min = sorted.slice(0, sorted.indexOf(min));
  const sorted_max = sorted.slice(sorted.indexOf(max) + 1);
  return sorted_min.concat(sorted_max);
}
