// You should find the strongest extension and return a string in this 
// format: ClassName.StrongestExtensionName.
// If there are two or more extensions with the same strength, you should
// choose the one that comes first in the array.
// For example, if you are given "Slices" as the class and an array of the
// extensions: ['SErviNGSliCes', 'Cheese', 'StuFfed'] then you should
// return 'Slices.SErviNGSliCes' since 'SErviNGSliCes' is the strongest extension 
// (its strength is -1).
// Example:
// >>> Strongest_Extension("my_class", ["AA", "Be", "CC"])
// "my_class.AA"
function Strongest_Extension(class_name: string, extensions: string[]): string {
    let strongest_extension = extensions[0];
    for (let i = 1; i < extensions.length; i++) {
        let extension = extensions[i];
        let extension_strength = 0;
        for (let j = 0; j < extension.length; j++) {
            if (extension[j] >= 65 && extension[j] <= 90) {
                extension_strength -= 1;
            } else if (extension[j] >= 97 && extension[j] <= 122) {
                extension_strength += 1;
            }
        }
        if (extension_strength < 0) {
            strongest_extension = extension;
        }
    }
    return class_name + "." + strongest_extension;
}
//You are given an array of two strings, both strings consist of open
// parentheses '(' or close parentheses ')' only.
// Your job is to check if it is possible to concatenate the two strings in
// some order, that the resulting string will be good.
// A string S is considered to be good if and only if all parentheses in S
// are balanced. For example: the string '(())()' is good, while the string
// '())' is not.
// Return 'Yes' if there's a way to make a good string, and return 'No' otherwise.
// Examples:
// >>> match_parens(["()(", ")"])
// "Yes"
// >>> match_parens([")", ")"])
// "No"
function match_parens(lst: string[]): string {
  const stack: string[] = [];
  for (const char of lst) {
    if (char === "(") {
      stack.push(char);
    } else {
      if (stack.length === 0) {
        return "No";
      }
      stack.pop();
    }
  }
  if (stack.length === 0) {
    return "Yes";
  }
  return "No";
}
