// 131
// >>> digitSum("aAaaaXa")
// 153
function digitSum(s: string): number {
  let sum = 0;
  for (let i = 0; i < s.length; i++) {
    if (s.charCodeAt(i) >= 65 && s.charCodeAt(i) <= 90) {
      sum += s.charCodeAt(i) - 64;
