//Input to this function is a string containing multiple groups of nested parentheses. Your goal is to
// separate those group into separate strings and return the array of those.
// Separate groups are balanced (each open brace is properly closed) and not nested within each other
// Ignore any spaces in the input string.
// >>> separate_paren_groups("( ) (( )) (( )( ))")
// ["()", "(())", "(()())"]
function separate_paren_groups(paren_string: string): string[] {
  const paren_array: string[] = [];
  let paren_group: string = "";
  let paren_group_count: number = 0;
  for (let i = 0; i < paren_string.length; i++) {
    if (paren_string[i] === "(") {
      paren_group_count++;
      paren_group += paren_string[i];
    } else if (paren_string[i] === ")") {
      paren_group_count--;
      paren_group += paren_string[i];
    }
    if (paren_group_count === 0) {
      paren_array.push(paren_group);
      paren_group = "";
    }
  }
  return paren_array;
}
