// false
// >>> check_if_last_char_is_a_letter("")
// false
function check_if_last_char_is_a_letter(txt: string): boolean {
  const lastChar = txt[txt.length - 1];
  return (
    lastChar.match(/[a-zA-Z]/g) !== null &&
    lastChar.match(/[a-zA-Z]/) !== null &&
    lastChar.match(/[a-zA-Z]/) !== lastChar
  );
