// >>> valid_date("06-04-2020")
// true
// >>> valid_date("06/04/2020")
// false
function valid_date(date: string): boolean {
  const date_split = date.split("-");
  const month = date_split[0];
  const day = date_split[1];
  const year = date_split[2];
  const date_split_length = date_split.length;
  if (date_split_length === 3) {
    if (
      month.length === 2 &&
      day.length === 2 &&
      year.length === 4 &&
      Number(month) >= 1 &&
      Number(month) <= 12 &&
      Number(day) >= 1 &&
      Number(day) <= 31 &&
      Number(year) >= 1900 &&
      Number(year) <= 2020
    ) {
      return true;
    } else {
      return false;
    }
  } else {
    return false;
  }
}
