// find any vowel met the above condition. 
// You may assume that the given string contains English letter only.
// Example:
// >>> get_closest_vowel("yogurt")
// "u"
// >>> get_closest_vowel("FULL")
// "U"
// >>> get_closest_vowel("quick")
// ""
// >>> get_closest_vowel("ab")
// ""
function get_closest_vowel(word: string): string {
    let vowels = ['a', 'e', 'i', 'o', 'u']
    let vowel_index = word.lastIndexOf('a')
    if (vowel_index === -1) {
        vowel_index = word.lastIndexOf('e')
    }
    if (vowel_index === -1) {
        vowel_index = word.lastIndexOf('i')
    }
    if (vowel_index === -1) {
        vowel_index = word.lastIndexOf('o')
    }
    if (vowel_index === -1) {
        vowel_index = word.lastIndexOf('u')
    }
    if (vowel_index === -1) {
        return ''
    }
    let vowel = word[vowel_index]
    let vowel_index_left = vowel_index - 1
    let vowel_index_right = vowel_index + 1
    if (vowel_index_left < 0) {
        vowel_index_left = 0
    }
    if (vowel_index_right >= word.length) {
        vowel_index_right = word.length - 1
    }
    let left_consonant = word[vowel_index_left]
    let right_consonant = word[vowel_index_right]
    if (vowels.indexOf(left_consonant) !== -1 && vowels.indexOf(right_consonant) !== -1) {
        return vowel
    }
    return ''
}
