// of the letter with the most repetition and containing the corresponding count.
// If several letters have the same occurrence, return all of them.
// Example:
// >>> histogram("a b c")
// {"a": 1, "b": 1, "c": 1}
// >>> histogram("a b b a")
// {"a": 2, "b": 2}
// >>> histogram("a b c a b")
// {"a": 2, "b": 2}
// >>> histogram("b b b b a")
// {"b": 4}
// >>> histogram("")
// {}
function histogram(test: string): {[key: string]: number} {
  const histogram: {[key: string]: number} = {};
  const words = test.split(' ');
  for (const word of words) {
    for (const letter of word) {
      if (histogram[letter] === undefined) {
        histogram[letter] = 0;
      }
      histogram[letter]++;
    }
  }
  return histogram;
}
