// >>> get_row([], 1)
// []
// >>> get_row([[], [1], [1, 2, 3]], 3)
// [[2, 2]]
function get_row(lst: number[][], x: number): [number, number][] {
  const result: [number, number][] = [];
  for (let i = 0; i < lst.length; i++) {
    const row = lst[i];
    for (let j = 0; j < row.length; j++) {
      if (row[j] === x) {
        result.push([i, j]);
