// no such element exists then return -1. The given array will not contain
// duplicate values.
// Examples:
// >>> can_arrange([1, 2, 4, 3, 5])
// 3
// >>> can_arrange([1, 2, 3])
// -1
function can_arrange(arr: number[]): number {
  let i = 0;
  let j = arr.length - 1;
  while (i < j) {
    if (arr[i] >= arr[j]) {
      return -1;
    }
    i++;
    j--;
  }
  return i;
}
