//Return a greatest common divisor of two integers a and b
// >>> greatest_common_divisor(3, 5)
// 1
// >>> greatest_common_divisor(25, 15)
// 5
function greatest_common_divisor(a: number, b: number): number {
    if (a < b) {
        [a, b] = [b, a];
    }
    let r = 1;
    while (a % b !== 0) {
        [a, b] = [b, a % b];
        r++;
    }
    return r;
}
//Given a string of words, return an array of words split on whitespace, if no whitespaces exists in the text you
// should split on commas ',' if no commas exists you should return the number of lower-case letters with odd order in the
// alphabet, ord('a') = 0, ord('b') = 1, ... ord('z') = 25
// Examples
// >>> split_words("Hello world!")
// ["Hello", "world!"]
// >>> split_words("Hello,world!")
// ["Hello", "world!"]
// >>> split_words("abcdef")
// 3
function split_words(txt: string): string[]| number {
  const words: string[] = [];
  let word = '';
  for (let i = 0; i < txt.length; i++) {
    if (txt[i] === ' ' || txt[i] === ',') {
      if (word.length > 0) {
        words.push(word);
      }
      word = '';
    } else {
      word += txt[i];
    }
  }
  if (word.length > 0) {
    words.push(word);
  }
  return words.length;
}

export default split_words;
//In this Kata, you have to sort an array of non-negative integers according to
// number of ones in their binary representation in ascending order.
// For similar number of ones, sort based on decimal value.
// It must be implemented like this:
// >>> sort_array([1, 5, 2, 3, 4])
// [1, 2, 3, 4, 5]
// >>> sort_array([-2, -3, -4, -5, -6])
// [-6, -5, -4, -3, -2]
// >>> sort_array([1, 0, 2, 3, 4])
// [0, 1, 2, 3, 4]
function sort_array(arr: number[]): number[] {
  const zeros = arr.filter((n) => n === 0);
  const ones = arr.filter((n) => n !== 0);
  const zeros_sorted = zeros.sort((a, b) => a - b);
  const ones_sorted = ones.sort((a, b) => a - b);
  const sorted = zeros_sorted.concat(ones_sorted);
  return sorted;
}

export default sort_array;
