// "string"
// >>> find_max(["name", "enam", "game"])
// "enam"
// >>> find_max(["aaaaaaa", "bb", "cc"])
// "aaaaaaa"
function find_max(words: string[]): string {
  const map = new Map<string, number>();
  for (const word of words) {
    for (const char of word) {
      map.set(char, map.get(char) ? map.get(char) + 1 : 1);
    }
  }
  const max = Array.from(map.values()).reduce((a, b) => a > b ? a : b);
  const max_word = words.find((word) => {
    for (const char of word) {
      if (map.get(char) === max) {
        return word;
      }
    }
  });
  return max_word;
}
//Given a string 'text', return its md5 hash equivalent string.
// If 'text' is an empty string, return undefined.
// >>> string_to_md5("Hello world")
// "3e25960a79dbc69b674cd4ec67a72c62"
function string_to_md5(text: string): string | undefined {
  if (text.length === 0) {
    return undefined;
  }
  const hash = crypto.createHash('md5');
  hash.update(text);
  return hash.digest('hex');
}
