// to the values of the even indicies of l, but sorted.
// >>> sort_even([1, 2, 3])
// [1, 2, 3]
// >>> sort_even([5, 6, 3, 4])
// [3, 6, 5, 4]
function sort_even(l: number[]): number[] {
    let l_even = []
    let l_odd = []
    for (let i = 0; i < l.length; i++) {
        if (i % 2 == 0) {
            l_even.push(l[i])
        } else {
            l_odd.push(l[i])
        }
    }
    l_odd.sort()
    return l_even.concat(l_odd)
}
