    let buckets = new Array(rows).fill(0);
    let res = 0;
    let bucket = 0;
    for (let i = 0; i < rows; i++) {
        for (let j = 0; j < cols; j++) {
            if (grid[i][j] === 1) {
                buckets[i] += 1;
            }
        }
    }
    for (let i = 0; i < rows; i++) {
        if (buckets[i] > capacity) {
            res += 1;
            buckets[i] -= capacity;
        } else {
            buckets[i] = 0;
        }
    }
    return res;
