// true
// >>> is_nested("[]]]]]]][[[[[]")
// false
// >>> is_nested("[][]")
// false
// >>> is_nested("[]")
// false
// >>> is_nested("[[][]]")
// true
// >>> is_nested("[[]][[")
// true
function is_nested(string: string): boolean {
    let stack: string[] = [];
    for (let i = 0; i < string.length; i++) {
        if (string[i] === "[") {
            stack.push(string[i]);
        } else if (string[i] === "]") {
            if (stack.length === 0) {
                return false;
            }
            let top = stack.pop();
            if (top === "[") {
                stack.push(string[i]);
            } else {
                return false;
            }
        }
    }
    return stack.length === 0;
}
//You are given two positive integers n and m, and your task is to compute the
// average of the integers from n through m (including n and m). 
// Round the answer to the nearest integer and convert that to binary.
// If n is greater than m, return -1.
// Example:
// >>> rounded_avg(1, 5)
// "0b11"
// >>> rounded_avg(7, 5)
// -1
// >>> rounded_avg(10, 20)
// "0b1111"
// >>> rounded_avg(20, 33)
// "0b11010"
function rounded_avg(n: number, m: number): string| number {
  if (n > m) return -1;
  let sum = 0;
  for (let i = n; i <= m; i++) {
    sum += i;
  }
  return (sum / (m - n + 1)) | 0;
}
