// 2
// >>> fizz_buzz(79)
// 3
function fizz_buzz(n: number): number {
  let count = 0;
  for (let i = 1; i < n; i++) {
    if (i % 11 === 0 || i % 13 === 0) {
      count++;
    }
  }
  return count;
}
