// Sentences are delimited by '.', '?' or '!'.
// For example:
// >>> is_bored("Hello world")
// 0
// >>> is_bored("The sky is blue. The sun is shining. I love this weather")
// 1
function is_bored(S: string): number {
    let bored = 0;
    let words = S.split(" ");
    for (let i = 0; i < words.length; i++) {
        if (words[i] == "I") {
            bored++;
        }
    }
    return bored;
}
