// are balanced. For example: the string '(())()' is good, while the string
// '())' is not.
// Return 'Yes' if there's a way to make a good string, and return 'No' otherwise.
// Examples:
// >>> match_parens(["()(", ")"])
// "Yes"
// >>> match_parens([")", ")"])
// "No"
function match_parens(lst: string[]): string {
    let stack: string[] = [];
    for (let i = 0; i < lst.length; i++) {
        if (lst[i] === "(") {
            stack.push(lst[i]);
        } else {
            if (stack.length === 0) {
                return "No";
            }
            const top = stack.pop();
            if (top === "(") {
                continue;
            } else {
                return "No";
            }
        }
    }
    if (stack.length === 0) {
        return "Yes";
    } else {
        return "No";
    }
}
