function valid_date(date: string): boolean {
  const date_parts = date.split("-");
  if (date_parts.length !== 3) {
    return false;
  }
  const month = parseInt(date_parts[0], 10);
  const day = parseInt(date_parts[1], 10);
  const year = parseInt(date_parts[2], 10);
  if (month < 1 || month > 12) {
    return false;
  }
  if (day < 1 || day > 31) {
    if (month === 2) {
      if (year % 4 === 0 && year % 100 !== 0) {
        return day < 29;
      }
      return day < 28;
    }
    return day < 30;
  }
  return true;
}
