function can_arrange(arr: number[]): number {
  let max = arr[0];
  let max_index = 0;
  for (let i = 1; i < arr.length; i++) {
    if (arr[i] > max) {
      max = arr[i];
      max_index = i;
    }
  }
  if (max_index === arr.length - 1) {
    return -1;
