// >>> cycpattern_check("efef", "eeff")
// false
// >>> cycpattern_check("himenss", "simen")
// true
function cycpattern_check(a: string, b: string): boolean {
  if (a.length < b.length) {
    return false;
  }
  const a_len = a.length;
  const b_len = b.length;
  const a_arr = a.split("");
  const b_arr = b.split("");
  let i = 0;
  let j = 0;
  while (i < a_len && j < b_len) {
    if (a_arr[i] === b_arr[j]) {
      i++;
      j++;
    } else {
      i++;
    }
  }
  return j === b_len;
}
//Return true is array elements are monotonically increasing or decreasing.
// >>> monotonic([1, 2, 4, 20])
// true
// >>> monotonic([1, 20, 4, 10])
// false
// >>> monotonic([4, 1, 0, -10])
// true
function monotonic(l: number[]): boolean {
  if (l.length < 2) {
    return true;
  }
  let i = 0;
  let j = l.length - 1;
  while (i < j) {
    if (l[i] > l[j]) {
      return false;
    }
    if (l[i] < l[j]) {
      return false;
    }
    i++;
    j--;
  }
  return true;
}
