//Input to this function is a string represented multiple groups for nested parentheses separated by spaces.
// For each of the group, output the deepest level of nesting of parentheses.
// E.g. (()()) has maximum two levels of nesting while ((())) has three.
// >>> parse_nested_parens("(()()) ((())) () ((())()())")
// [2, 3, 1, 3]
function parse_nested_parens(paren_string: string): number[] {
    let paren_stack: number[] = [];
    let max_nesting: number = 0;
    let paren_string_length: number = paren_string.length;
    for (let i = 0; i < paren_string_length; i++) {
        let paren: string = paren_string[i];
        if (paren === "(") {
            paren_stack.push(i);
        } else if (paren === ")") {
            let popped_index: number = paren_stack.pop();
            if (popped_index === undefined) {
                throw new Error("Unbalanced parentheses");
            }
            let nesting_level: number = i - popped_index;
            if (nesting_level > max_nesting) {
                max_nesting = nesting_level;
            }
        }
    }
    return paren_stack.reverse().map(index => index - paren_string_length);
}
