//Complete the function that takes two integers and returns 
// the product of their unit digits.
// Assume the input is always valid.
// Examples:
// >>> multiply(148, 412)
// 16
// >>> multiply(19, 28)
// 72
// >>> multiply(2020, 1851)
// 0
// >>> multiply(14, -15)
// 20
function multiply(a: number, b: number): number {
    let result = 0;
    let a_digits = a.toString().split("");
    let b_digits = b.toString().split("");
    let a_length = a_digits.length;
    let b_length = b_digits.length;
    let max_length = Math.max(a_length, b_length);
    for (let i = 0; i < max_length; i++) {
        let a_digit = a_digits[i] ? Number(a_digits[i]) : 0;
        let b_digit = b_digits[i] ? Number(b_digits[i]) : 0;
        result += a_digit * b_digit;
    }
    return result;
}
//For a given array of input numbers, calculate Mean Absolute Deviation
// around the mean of this dataset.
// Mean Absolute Deviation is the average absolute difference between each
// element and a centerpoint (mean in this case):
// MAD = average | x - x_mean |
// >>> mean_absolute_deviation([1.0, 2.0, 3.0, 4.0])
// 1.0
function mean_absolute_deviation(numbers: number[]): number {
  let sum = 0;
  for (let i = 0; i < numbers.length; i++) {
    sum += Math.abs(numbers[i] - mean(numbers));
  }
  return sum / numbers.length;
}
