// [2, 3]
function common(l1: number[], l2: number[]): number[] {
  const l1Set = new Set(l1);
  const l2Set = new Set(l2);
  const commonSet = new Set([...l1Set].filter(x => l2Set.has(x)));
  return Array.from(commonSet);
}
