// "cdxxvi"
function int_to_mini_roman(number: number): string {
  let result = '';
  let roman = [
    ['M', 1000],
    ['CM', 900],
    ['D', 500],
    ['CD', 400],
    ['C', 100],
    ['XC', 90],
    ['L', 50],
    ['XL', 40],
    ['X', 10],
    ['IX', 9],
    ['V', 5],
    ['IV', 4],
    ['I', 1],
  ];
  for (let i = 0; i < roman.length; i++) {
    let [roman_letter, roman_value] = roman[i];
    while (number >= roman_value) {
      result += roman_letter;
      number -= roman_value;
    }
  }
  return result;
}
