//Return a greatest common divisor of two integers a and b
// >>> greatest_common_divisor(3, 5)
// 1
// >>> greatest_common_divisor(25, 15)
// 5
function greatest_common_divisor(a: number, b: number): number {
    if (a === 0) {
        return b;
    }
    if (b === 0) {
        return a;
    }
    if (a === b) {
        return a;
    }
    if (a < b) {
        return greatest_common_divisor(a, b - a);
    }
    return greatest_common_divisor(b, a - b);
}
//Given a string of words, return an array of words split on whitespace, if no whitespaces exists in the text you
// should split on commas ',' if no commas exists you should return the number of lower-case letters with odd order in the
// alphabet, ord('a') = 0, ord('b') = 1, ... ord('z') = 25
// Examples
// >>> split_words("Hello world!")
// ["Hello", "world!"]
// >>> split_words("Hello,world!")
// ["Hello", "world!"]
// >>> split_words("abcdef")
// 3
function split_words(txt: string): string[]| number {
  const words: string[] = [];
  let word = '';
  let i = 0;
  while (i < txt.length) {
    const c = txt[i];
    if (c === ' ') {
      if (word.length > 0) {
        words.push(word);
        word = '';
      }
    } else if (c === ',') {
      if (word.length > 0) {
        words.push(word);
        word = '';
      }
    } else {
      word += c;
    }
    i++;
  }
  if (word.length > 0) {
    words.push(word);
  }
  return words.length > 0 ? words : words.length;
}
