// >>> sort_array([1, 0, 2, 3, 4])
// [0, 1, 2, 3, 4]
function sort_array(arr: number[]): number[] {
  return arr.sort((a, b) => {
    const a_ones = a.toString(2).split('1').length - 1;
    const b_ones = b.toString(2).split('1').length - 1;
    if (a_ones > b_ones) {
      return 1;
    } else if (a_ones < b_ones) {
      return -1;
    } else {
      return a - b;
    }
  });
}

