// l' is identical to l in the odd indicies, while its values at the even indicies are equal
// to the values of the even indicies of l, but sorted.
// >>> sort_even([1, 2, 3])
// [1, 2, 3]
// >>> sort_even([5, 6, 3, 4])
// [3, 6, 5, 4]
function sort_even(l: number[]): number[] {
    let l_even = [];
    let l_odd = [];
    for (let i = 0; i < l.length; i++) {
        if (i % 2 == 0) {
            l_even.push(l[i]);
        } else {
            l_odd.push(l[i]);
        }
    }
    l_even.sort();
    let l_even_sorted = [];
    for (let i = 0; i < l_even.length; i++) {
        l_even_sorted.push(l_even[i]);
    }
    let l_odd_sorted = [];
    for (let i = 0; i < l_odd.length; i++) {
        l_odd_sorted.push(l_odd[i]);
    }
    let l_sorted = [];
    for (let i = 0; i < l_even_sorted.length; i++) {
        l_sorted.push(l_even_sorted[i]);
    }
    for (let i = 0; i < l_odd_sorted.length; i++) {
        l_sorted.push(l_odd_sorted[i]);
    }
    return l_sorted;
}
//Check if two words have the same characters.
// >>> same_chars("eabcdzzzz", "dddzzzzzzzddeddabc")
// true
// >>> same_chars("abcd", "dddddddabc")
// true
// >>> same_chars("dddddddabc", "abcd")
// true
// >>> same_chars("eabcd", "dddddddabc")
// false
// >>> same_chars("abcd", "dddddddabce")
// false
// >>> same_chars("eabcdzzzz", "dddzzzzzzzddddabc")
// false
function same_chars(s0: string, s1: string): boolean {
    if (s0.length !== s1.length) {
        return false;
    }
    for (let i = 0; i < s0.length; i++) {
        if (s0[i] !== s1[i]) {
            return false;
        }
    }
    return true;
}
//brackets is a string of "<" and ">".
// return true if every opening bracket has a corresponding closing bracket.
// >>> correct_bracketing("<")
// false
// >>> correct_bracketing("<>")
// true
// >>> correct_bracketing("<<><>>")
// true
// >>> correct_bracketing("><<>")
// false
function correct_bracketing(brackets: string): boolean {
  let stack: string[] = [];
  for (let i = 0; i < brackets.length; i++) {
    if (brackets[i] === "<") {
      stack.push("<");
    } else if (brackets[i] === ">") {
      if (stack.length === 0) {
        return false;
      }
      stack.pop();
    }
  }
  return stack.length === 0;
}
