}
//Create a function encrypt that takes a string as an argument and
// returns a string encrypted with the alphabet being rotated. 
// The alphabet should be rotated in a manner such that the letters 
// shift down by two multiplied to two places.
// For example:
// >>> encrypt("hi")
// "lm"
// >>> encrypt("asdfghjkl")
// "ewhjklnop"
// >>> encrypt("gf")
// "kj"
// >>> encrypt("et")
// "ix"
function encrypt(s: string): string {
    let result = "";
    for (let i = 0; i < s.length; i++) {
        let char = s[i];
        if (char.charCodeAt(0) >= 65 && char.charCodeAt(0) <= 90) {
            result += String.fromCharCode((char.charCodeAt(0) + 2) % 26 + 65);
        } else if (char.charCodeAt(0) >= 97 && char.charCodeAt(0) <= 122) {
            result += String.fromCharCode((char.charCodeAt(0) + 2) % 26 + 97);
        } else {
            result += char;
        }
    }
    return result;
}
//Given an object, return true if all keys are strings in lower 
// case or all keys are strings in upper case, else return false.
// The function should return false is the given object is empty.
// Examples:
// >>> check_dict_case({"a": "apple", "b": "banana"})
// true
// >>> check_dict_case({"a": "apple", "A": "banana", "B": "banana"})
// false
// >>> check_dict_case({"a": "apple", 8: "banana", "a": "apple"})
// false
// >>> check_dict_case({"Name": "John", "Age": "36", "City": "Houston"})
// false
// >>> check_dict_case({"STATE": "NC", "ZIP": "12345"})
// true
function check_dict_case(dict: {[key: string]: string}): boolean {
    let lowerCase = true;
    let upperCase = true;
    for (let key in dict) {
        if (dict[key].toLowerCase() !== dict[key]) {
            lowerCase = false;
        }
        if (dict[key].toUpperCase() !== dict[key]) {
            upperCase = false;
        }
    }
    return lowerCase || upperCase;
}
//Given a non-empty array of integers lst. add the even elements that are at odd indices..
// Examples:
// >>> add([4, 2, 6, 7])
// 2
function add(lst: number[]): number {
  let sum = 0;
  for (let i = 0; i < lst.length; i++) {
    if (i % 2 === 1) {
      sum += lst[i];
    }
  }
  return sum;
}
//Given a string text, replace all spaces in it with underscores, 
// and if a string has more than 2 consecutive spaces, 
// then replace all consecutive spaces with - 
// >>> fix_spaces(" Example")
// "Example"
// >>> fix_spaces(" Example 1")
// "Example_1"
// >>> fix_spaces(" Example 2")
// "_Example_2"
// >>> fix_spaces(" Example 3")
// "_Example-3"
function fix_spaces(text: string): string {
    return text.replace(/\s+/g, (match) => {
        if (match.length > 2) {
            return '-';
        } else {
            return '_';
        }
    });
}
//The FibFib number sequence is a sequence similar to the Fibbonacci sequnece that's defined as follows:
// fibfib(0) == 0
// fibfib(1) == 0
// fibfib(2) == 1
// fibfib(n) == fibfib(n-1) + fibfib(n-2) + fibfib(n-3).
// Please write a function to efficiently compute the n-th element of the fibfib number sequence.
// >>> fibfib(1)
// 0
// >>> fibfib(5)
// 4
// >>> fibfib(8)
// 24
function fibfib(n: number): number {
  if (n === 0) return 0;
  if (n === 1) return 0;
  if (n === 2) return 1;
  let fibfibArray: number[] = [0, 0, 1];
  for (let i = 3; i <= n; i++) {
    fibfibArray[i] = fibfibArray[i - 1] + fibfibArray[i - 2] + fibfibArray[i - 3];
  }
  return fibfibArray[n];
}
//Given an array of numbers, return the sum of squares of the numbers
// in the array that are odd. Ignore numbers that are negative or not integers.
// >>> double_the_difference([1, 3, 2, 0])
// 10
// >>> double_the_difference([-1, -2, 0])
// 0
// >>> double_the_difference([9, -2])
// 81
// >>> double_the_difference([0])
// 0
// If the input array is empty, return 0.
function double_the_difference(lst: number[]): number {
  let sum = 0;
  for (let i = 0; i < lst.length; i++) {
    if (lst[i] % 2 === 1) {
      sum += lst[i] ** 2;
    }
  }
  return sum;
}
//Filter given array of any tsthon values only for integers
// >>> filter_integers(["a", 3.14, 5])
// [5]
// >>> filter_integers([1, 2, 3, "abc", {}, []])
// [1, 2, 3]
function filter_integers(values: any[]): number[] {
    return values.filter(function (value) {
        return typeof value === "number" && value % 1 === 0;
    });
}
//Imagine a road that's a perfectly straight infinitely long line.
// n cars are driving left to right;  simultaneously, a different set of n cars
// are driving right to left.   The two sets of cars start out being very far from
// each other.  All cars move in the same speed.  Two cars are said to collide
// when a car that's moving left to right hits a car that's moving right to left.
// However, the cars are infinitely sturdy and strong; as a result, they continue moving
// in their trajectory as if they did not collide.
// This function outputs the number of such collisions.
function car_race_collision(n: number): number {
  let collisions = 0;
  for (let i = 1; i <= n; i++) {
    if (i % 2 === 0) {
      collisions++;
    }
  }
  return collisions;
}
//Input to this function is a string representing musical notes in a special ASCII format.
// Your task is to parse this string and return array of integers corresponding to how many beats does each
// not last.
// Here is a legend:
// 'o' - whole note, lasts four beats
// 'o|' - half note, lasts two beats
// '.|' - quater note, lasts one beat
// >>> parse_music("o o| .| o| o| .| .| .| .| o o")
// [4, 2, 1, 2, 2, 1, 1, 1, 1, 4, 4]
function parse_music(music_string: string): number[] {
  const notes = music_string.split(" ");
  const note_lengths = [];
  for (let i = 0; i < notes.length; i++) {
    const note = notes[i];
    if (note === "o") {
      note_lengths.push(4);
    } else if (note === "o|") {
      note_lengths.push(2);
    } else if (note === ".|") {
      note_lengths.push(1);
    }
  }
  return note_lengths;
}
//You will be given a number in decimal form and your task is to convert it to
