//Given two arrays operator, and operand. The first array has basic algebra operations, and 
// the second array is an array of integers. Use the two given arrays to build the algebric 
// expression and return the evaluation of this expression.
// The basic algebra operations:
// Addition ( + ) 
// Subtraction ( - ) 
// Multiplication ( * ) 
// Floor division ( // ) 
// Exponentiation ( ** ) 
// Example:
// operator['+', '*', '-']
// array = [2, 3, 4, 5]
// result = 2 + 3 * 4 - 5
// => result = 9
// Note:
// The length of operator array is equal to the length of operand array minus one.
// Operand is an array of of non-negative integers.
// Operator array has at least one operator, and operand array has at least two operands.
function do_algebra(operator: string[], operand: number[]): number {
    let result = operand[0];
    for (let i = 0; i < operator.length; i++) {
        if (operator[i] === '+') {
            result += operand[i + 1];
        } else if (operator[i] === '-') {
            result -= operand[i + 1];
        } else if (operator[i] === '*') {
            result *= operand[i + 1];
        } else if (operator[i] === '//') {
            result = Math.floor(result / operand[i + 1]);
        } else if (operator[i] === '**') {
            result = Math.pow(result, operand[i + 1]);
        }
    }
    return result;
}
//For a given string, flip lowercase characters to uppercase and uppercase to lowercase.
// >>> flip_case("Hello")
// "hELLO"
function flip_case(string: string): string {
  return string.split("").map(function (char) {
    if (char === char.toLowerCase()) {
      return char.toUpperCase();
    } else {
      return char.toLowerCase();
    }
  }).join("");
}
//Given an array of integers, sort the integers that are between 1 and 9 inclusive,
// reverse the resulting array, and then replace each digit by its corresponding name from
// "One", "Two", "Three", "Four", "Five", "Six", "Seven", "Eight", "Nine".
// For example:
// >>> by_length([2, 1, 1, 4, 5, 8, 2, 3])
// ["Eight", "Five", "Four", "Three", "Two", "Two", "One", "One"]
// If the array is empty, return an empty array:
// >>> by_length([])
// []
// If the array has any strange number ignore it:
// >>> by_length([1, -1, 55])
// ["One"]
function by_length(arr: number[]): string[] {
  const result: string[] = [];
  for (let i = 0; i < arr.length; i++) {
    if (arr[i] >= 1 && arr[i] <= 9) {
      result.push(arr[i].toString());
    }
  }
  return result.reverse();
}
//Return array of prime factors of given integer in the order from smallest to largest.
// Each of the factors should be arrayed number of times corresponding to how many times it appeares in factorization.
// Input number should be equal to the product of all factors
// >>> factorize(8)
// [2, 2, 2]
// >>> factorize(25)
// [5, 5]
// >>> factorize(70)
// [2, 5, 7]
function factorize(n: number): number[] {
  const factors: number[] = [];
  let i = 2;
  while (n > 1) {
    if (n % i === 0) {
      factors.push(i);
      n /= i;
    } else {
      i++;
    }
  }
  return factors;
}
