// >>> total_match(["4"], ["1", "2", "3", "4", "5"])
// ["4"]
function total_match(lst1: string[], lst2: string[]): string[] {
    let total1 = 0;
    let total2 = 0;
    for (let i = 0; i < lst1.length; i++) {
        total1 += lst1[i].length;
    }
    for (let i = 0; i < lst2.length; i++) {
        total2 += lst2[i].length;
    }
    if (total1 < total2) {
        return lst1;
    } else if (total1 > total2) {
        return lst2;
    } else {
        return lst1;
    }
}
//Return maximum element in the array.
// >>> max_element([1, 2, 3])
// 3
// >>> max_element([5, 3, -5, 2, -3, 3, 9, 0, 123, 1, -10])
// 123
function max_element(l: number[]): number {
    let max = l[0];
    for (let i = 1; i < l.length; i++) {
        if (l[i] > max) {
            max = l[i];
        }
    }
    return max;
}
//Create a function that takes a string as input which contains only square brackets.
// The function should return true if and only if there is a valid subsequence of brackets 
// where at least one bracket in the subsequence is nested.
// >>> is_nested("[[]]")
// true
// >>> is_nested("[]]]]]]][[[[[]")
// false
// >>> is_nested("[][]")
// false
// >>> is_nested("[]")
// false
// >>> is_nested("[[][]]")
// true
// >>> is_nested("[[]][[")
// true
function is_nested(string: string): boolean {
    let stack: string[] = [];
    for (let i = 0; i < string.length; i++) {
        if (string[i] === '[') {
            stack.push(string[i]);
        } else {
            if (stack.length === 0) {
                return false;
            }
            stack.pop();
        }
    }
    return stack.length === 0;
}
//You are given two positive integers n and m, and your task is to compute the
// average of the integers from n through m (including n and m). 
// Round the answer to the nearest integer and convert that to binary.
// If n is greater than m, return -1.
// Example:
// >>> rounded_avg(1, 5)
// "0b11"
// >>> rounded_avg(7, 5)
// -1
// >>> rounded_avg(10, 20)
// "0b1111"
// >>> rounded_avg(20, 33)
// "0b11010"
function rounded_avg(n: number, m: number): string| number {
    if (n > m) {
        return -1;
    }
    let sum = 0;
    for (let i = n; i <= m; i++) {
        sum += i;
    }
    let avg = sum / (m - n + 1);
    let avg_round = Math.round(avg);
    let avg_bin = avg_round.toString(2);
    return avg_bin;
}
