// For example:
// tri(2) = 1 + (2 / 2) = 2
// tri(4) = 3
// tri(3) = tri(2) + tri(1) + tri(4)
// = 2 + 3 + 3 = 8 
// You are given a non-negative integer number n, you have to a return an array of the 
// first n + 1 numbers of the Tribonacci sequence.
// Examples:
// >>> tri(3)
// [1, 3, 2, 8]
function tri(n: number): number[] {
  const result: number[] = [];
  let a = 1;
  let b = 3;
  let c = 2;
  for (let i = 0; i <= n; i++) {
    if (i % 2 === 0) {
      result.push(a + (i / 2));
    } else {
      result.push(a + b + c);
      [a, b, c] = [b, c, a + b + c];
    }
  }
  return result;
}
