// [-4, -3, 5]
// Example 2:
// >>> maximum([4, -4, 4], 2)
// [4, 4]
// Example 3:
// >>> maximum([-3, 2, 1, 2, -1, -2, 1], 1)
// [2]
// Note:
// 1. The length of the array will be in the range of [1, 1000].
// 2. The elements in the array will be in the range of [-1000, 1000].
// 3. 0 <= k <= len(arr)
function maximum(arr: number[], k: number): number[] {
  const result: number[] = [];
  const maxHeap: number[] = [];
  for (let i = 0; i < arr.length; i++) {
    if (maxHeap.length < k) {
      maxHeap.push(arr[i]);
      heapify(maxHeap, maxHeap.length - 1);
    } else if (arr[i] > maxHeap[0]) {
      maxHeap[0] = arr[i];
      heapify(maxHeap, 0);
    }
  }
  while (maxHeap.length) {
    result.push(maxHeap.pop()!);
  }
  return result;
}
//For a given number n, find the largest number that divides n evenly, smaller than n
// >>> largest_divisor(15)
// 5
function largest_divisor(n: number): number {
  for (let i = n - 1; i > 0; i--) {
    if (n % i === 0) {
      return i;
    }
  }
  return 1;
}
//Given an array of non-negative integers, return a cots of the given array after sorting,
// you will sort the given array in ascending order if the sum( first index value, last index value) is odd,
// or sort it in descending order if the sum( first index value, last index value) is even.
// Note:
// * don't change the given array.
// Examples:
// >>> sort_array([])
// []
// >>> sort_array([5])
// [5]
// >>> sort_array([2, 4, 3, 0, 1, 5])
// [0, 1, 2, 3, 4, 5]
// >>> sort_array([2, 4, 3, 0, 1, 5, 6])
// [6, 5, 4, 3, 2, 1, 0]
function sort_array(array: number[]): number[] {
  if (array.length === 0) {
    return [];
  }
  if (array.length === 1) {
    return array;
  }
  let sum = array[0] + array[array.length - 1];
  if (sum % 2 === 0) {
    return array.sort((a, b) => b - a);
  } else {
    return array.sort((a, b) => a - b);
  }
}
//Implement the function f that takes n as a parameter,
// and returns an array of size n, such that the value of the element at index i is the factorial of i if i is even
// or the sum of numbers from 1 to i otherwise.
// i starts from 1.
// the factorial of i is the multiplication of the numbers from 1 to i (1 * 2 * ... * i).
// Example:
// >>> f(5)
// [1, 2, 6, 24, 15]
function f(n: number): number[] {
  const result: number[] = [];
  for (let i = 1; i <= n; i++) {
    if (i % 2 === 0) {
      result.push(factorial(i));
    } else {
      result.push(sum(i));
    }
  }
  return result;
}
