// return 'Slices.SErviNGSliCes' since 'SErviNGSliCes' is the strongest extension 
// (its strength is -1).
// Example:
// >>> Strongest_Extension("my_class", ["AA", "Be", "CC"])
// "my_class.AA"
function Strongest_Extension(class_name: string, extensions: string[]): string {
    let strongest_extension: string = class_name;
    let strongest_extension_strength: number = 0;
    for (let extension of extensions) {
        let extension_strength: number = 0;
        for (let i = 0; i < extension.length; i++) {
            if (extension[i] >= 'A' && extension[i] <= 'Z') {
                extension_strength++;
            } else if (extension[i] >= 'a' && extension[i] <= 'z') {
                extension_strength--;
            }
        }
        if (extension_strength > strongest_extension_strength) {
            strongest_extension = class_name + "." + extension;
            strongest_extension_strength = extension_strength;
        }
    }
    return strongest_extension;
}
//You are given an array of two strings, both strings consist of open
// parentheses '(' or close parentheses ')' only.
// Your job is to check if it is possible to concatenate the two strings in
// some order, that the resulting string will be good.
// A string S is considered to be good if and only if all parentheses in S
// are balanced. For example: the string '(())()' is good, while the string
// '())' is not.
// Return 'Yes' if there's a way to make a good string, and return 'No' otherwise.
// Examples:
// >>> match_parens(["()(", ")"])
// "Yes"
// >>> match_parens([")", ")"])
// "No"
function match_parens(lst: string[]): string {
  let stack: string[] = [];
  for (let i = 0; i < lst.length; i++) {
    if (lst[i] === "(") {
      stack.push(lst[i]);
    } else {
      if (stack.length === 0) {
        return "No";
      }
      stack.pop();
    }
  }
  if (stack.length === 0) {
    return "Yes";
  } else {
    return "No";
  }
}
