// 2
// >>> next_smallest([])
// undefined
// >>> next_smallest([1, 1])
// undefined
function next_smallest(lst: number[]): number | undefined {
  if (lst.length < 2) {
    return undefined;
  }
  let min = lst[0];
  let minIndex = 0;
  for (let i = 1; i < lst.length; i++) {
    if (lst[i] < min) {
      min = lst[i];
      minIndex = i;
    }
  }
  let secondMin = lst[0];
  for (let i = 1; i < lst.length; i++) {
    if (i !== minIndex && lst[i] < secondMin) {
      secondMin = lst[i];
    }
  }
  return secondMin;
}
