//Return 2^n modulo p (be aware of numerics).
// >>> modp(3, 5)
// 3
// >>> modp(1101, 101)
// 2
// >>> modp(0, 101)
// 1
// >>> modp(3, 11)
// 8
// >>> modp(100, 101)
// 1
function modp(n: number, p: number): number {
  if (n < 0) {
    return modp(n + p, p);
  }
  if (n >= p) {
    return modp(n - p, p);
  }
  return n;
}
