// >>> histogram("a b b a")
// {"a": 2, "b": 2}
// >>> histogram("a b c a b")
// {"a": 2, "b": 2}
// >>> histogram("b b b b a")
// {"b": 4}
// >>> histogram("")
// {}
function histogram(test: string): {[key: string]: number} {
  const letters = test.split(" ");
  const letterCounts: {[key: string]: number} = {};
  letters.forEach((letter) => {
    if (letterCounts[letter]) {
      letterCounts[letter] += 1;
    } else {
      letterCounts[letter] = 1;
    }
  });
  return letterCounts;
}
