// [undefined, undefined]
// >>> largest_smallest_integers([0])
// [undefined, undefined]
function largest_smallest_integers(lst: number[]): [number | undefined, number | undefined] {
    let largest_negative: number | undefined = undefined;
    let smallest_positive: number | undefined = undefined;
    for (let i = 0; i < lst.length; i++) {
        if (lst[i] < 0) {
            if (largest_negative === undefined || lst[i] > largest_negative) {
                largest_negative = lst[i];
            }
        } else if (lst[i] > 0) {
            if (smallest_positive === undefined || lst[i] < smallest_positive) {
                smallest_positive = lst[i];
            }
        }
    }
    return [largest_negative, smallest_positive];
}
