// 2
// >>> vowels_count("ACEDY")
// 3
function vowels_count(s: string): number {
  let count = 0;
  for (let i = 0; i < s.length; i++) {
    if (s[i] === "a" || s[i] === "e" || s[i] === "i" || s[i] === "o" || s[i] === "u") {
      count++;
    }
  }
  if (s[s.length - 1] === "y") {
    count++;
  }
  return count;
}
