  for (let i = 0; i < words.length; i++) {
    let word = words[i];
    let count = 0;
    for (let j = 0; j < word.length; j++) {
      let char = word[j];
      if (word.indexOf(char) === j) {
        count++;
      }
    }
    if (count > max_count) {
      max = word;
      max_count = count;
    } else if (count === max_count && word < max) {
      max = word;
    }
  }
  return max;
}
