// 1
// >>> specialFilter([33, -2, -3, 45, 21, 109])
// 2
function specialFilter(nums: number[]): number {
    let count = 0;
    for (let i = 0; i < nums.length; i++) {
        if (nums[i] > 10 && (nums[i] % 10) % 2 === 1 && (Math.floor(nums[i] / 10)) % 2 === 1) {
