//You are given a non-empty array of positive integers. Return the greatest integer that is greater than 
// zero, and has a frequency greater than or equal to the value of the integer itself. 
// The frequency of an integer is the number of times it appears in the array.
// If no such a value exist, return -1.
// Examples:
// >>> search([4, 1, 2, 2, 3, 1])
// 2
// >>> search([1, 2, 2, 3, 3, 3, 4, 4, 4])
// 3
// >>> search([5, 5, 4, 4, 4])
// -1
function search(lst: number[]): number {
    let map: Map<number, number> = new Map();
    for (let i = 0; i < lst.length; i++) {
        if (map.has(lst[i])) {
            map.set(lst[i], map.get(lst[i])! + 1);
        } else {
            map.set(lst[i], 1);
        }
    }
    let max = 0;
    let maxKey = 0;
    for (let [key, value] of map) {
        if (value > max) {
            max = value;
            maxKey = key;
        }
    }
    if (maxKey > 0 && max >= maxKey) {
        return maxKey;
    } else {
        return -1;
    }
}
//brackets is a string of "(" and ")".
// return true if every opening bracket has a corresponding closing bracket.
// >>> correct_bracketing("(")
// false
// >>> correct_bracketing("()")
// true
// >>> correct_bracketing("(()())")
// true
// >>> correct_bracketing(")(()")
// false
function correct_bracketing(brackets: string): boolean {
  const stack: string[] = [];
  for (let i = 0; i < brackets.length; i++) {
    if (brackets[i] === "(") {
      stack.push(brackets[i]);
    } else {
      if (stack.length === 0) {
        return false;
      }
      stack.pop();
    }
  }
  return stack.length === 0;
}
//This function takes an array l and returns an array l' such that
// l' is identical to l in the odd indicies, while its values at the even indicies are equal
// to the values of the even indicies of l, but sorted.
// >>> sort_even([1, 2, 3])
// [1, 2, 3]
// >>> sort_even([5, 6, 3, 4])
// [3, 6, 5, 4]
function sort_even(l: number[]): number[] {
    let l_prime: number[] = [];
    for (let i = 0; i < l.length; i++) {
        if (i % 2 == 0) {
            l_prime.push(l[i]);
        } else {
            l_prime.push(l[i]);
            l_prime.push(l[i]);
        }
    }
    l_prime.sort((a, b) => a - b);
    return l_prime;
}
